'''

screenshots as a service -- persistence module responsible for connecting database and making DB communications for CRUD

'''
import logging
import sys
import pymongo
from persist import mp_persist
import multiprocessing as mp

from pymongo import MongoClient

#myyconfig = ScreenConfigurations()

log = logging.getLogger(__name__)


class ScreenShotDao:
    def __init__(self, config):
        log.info('-- ScreenShotDao class constructor --')
        self.config = config
        self.db_client = self.myget_screen_shots_db_con()

    def myget_screen_shots_db_con(self):
        log.info('-- screen shot as a service - persistence layer - get db conection')
        try:
            client = MongoClient(self.config.mongo_db_url)
            log.info('Connection Sucecssful')            
        except:
            log.error("Could not connect to MongoDB" + str(sys.exc_info()[0]) +str(sys.exc_info()[1]))

        else:
            return client

    def store_screen_shots(self, shots):        
        available_cpus = mp.cpu_count()
        print('Number of CPU on this machine:')
        print(available_cpus)
        pool = mp.Pool(available_cpus)
        #pool = mp.Pool()
        
        # persist with multiple processors
        pool.map(mp_persist, shots)

        pool.close();
        # to use single processng use below
        #mongo_client = self.db_client
        #screen_db = mongo_client.test
        #self.__persist(screen_db, shots)

    def retrieve_image(self, domain):
        mongo_client = self.db_client
        screen_db = mongo_client.test
        col = pymongo.collection.Collection(screen_db, self.config.screens_shots_collections)
        result = col.find_one({'domain': domain})
        return result.get('image', "") if result is not None else ""
        #client.retrieve(domain_url)
        # return ""

    def __persist_with_single_processor(self, screens_db, screen_shots):
        screen_collection = pymongo.collection.Collection(screens_db, self.config.screens_shots_collections)
        _ids = screen_collection.insert_many(screen_shots)
        return _ids
